let slideIndex=1,slides=document.getElementsByClassName("my-slide");function plusSlides(e){showSlides(slideIndex+=e)}function currentSlide(e){showSlides(slideIndex=e)}function showSlides(e){let s;for(e>slides.length&&(slideIndex=1),e<1&&(slideIndex=slides.length),s=0;s<slides.length;s++)slides[s].style.display="none";slides[slideIndex-1].style.display="block",displayNumbers(),document.getElementById("top-slide"+slideIndex).focus()}function displayNumbers(){document.getElementById("progress-numbers").textContent=slideIndex+" of "+slides.length}displayNumbers();